INSERT INTO `generator_template` VALUES ('f44f3ffa1317436a83de50895c327ba2', 'mapperSQL映射文件', 'xmlmapper', '0', '', 'E:\\testgen', '&lt;?xml version=&quot;1.0&quot; encoding=&quot;UTF-8&quot; ?&gt;\r\n&lt;!DOCTYPE mapper PUBLIC &quot;-//mybatis.org//DTD Mapper 3.0//EN&quot; &quot;http://mybatis.org/dtd/mybatis-3-mapper.dtd&quot; &gt;\r\n&lt;mapper namespace=&quot;${mapper.targetPackage}.${entityName?cap_first}Mapper&quot; &gt;\r\n\r\n	&lt;sql id=&quot;Base_Column_List&quot; &gt;\r\n		t.id,\r\n		t.name,\r\n		t.parent_id,\r\n		t.parent_ids,\r\n		t.parent_id AS parent_id,\r\n		p.name AS parent_name,\r\n	   &lt;#list columns as column&gt;\r\n	   &lt;#if column.columnName!=\'id\'&amp;&amp;column.columnName!=\'name\'&amp;&amp; column.columnName!=\'parent_id\'&amp;&amp; column.columnName!=\'parent_ids\'&gt;\r\n	    t.${column.columnName}&lt;#if !column.isBaseType&gt; AS \'${column.javaField}.id\'&lt;/#if&gt;,\r\n	   &lt;/#if&gt;\r\n	   &lt;/#list&gt;\r\n	   (select count(*) from ${tableName} s\r\n		WHERE s.parent_id=t.id) as\r\n		hasChildren\r\n	&lt;/sql&gt;\r\n	&lt;sql id=&quot;parentJoins&quot;&gt;\r\n		LEFT JOIN ${tableName} p ON p.id = t.parent_id\r\n	&lt;/sql&gt;\r\n    &lt;!-- 查找通过ID查找树信息 --&gt;\r\n	&lt;select id=&quot;selectByTreeId&quot; resultType=&quot;${entity.targetPackage}.${entityName?cap_first}&quot;\r\n		parameterType=&quot;java.lang.String&quot;&gt;\r\n		select\r\n		&lt;include refid=&quot;Base_Column_List&quot; /&gt;\r\n		from ${tableName} t\r\n		&lt;include refid=&quot;parentJoins&quot; /&gt;\r\n		where t.id = ${&quot;#&quot;}{id,jdbcType=VARCHAR}\r\n	&lt;/select&gt;\r\n	\r\n	&lt;!-- 查找树列表 --&gt;\r\n	&lt;select id=&quot;selectTreeList&quot; resultType=&quot;${entity.targetPackage}.${entityName?cap_first}&quot;&gt;\r\n		select\r\n		&lt;include refid=&quot;Base_Column_List&quot; /&gt;\r\n		from ${tableName} t\r\n		&lt;include refid=&quot;parentJoins&quot; /&gt;\r\n		&lt;where&gt;\r\n			${&quot;$&quot;}{ew.sqlSegment}\r\n		&lt;/where&gt;\r\n	&lt;/select&gt;\r\n\r\n	&lt;!-- 更新子树 --&gt;\r\n	&lt;update id=&quot;updateSunTreeParentIds&quot;&gt;\r\n		update ${tableName} set parent_ids=\r\n		CONCAT(${&quot;#&quot;}{newParentIds},substring(parent_ids,\r\n		length(${&quot;#&quot;}{oldParentIds})+1,length(parent_ids)+1))\r\n		where parent_ids like concat(${&quot;#&quot;}{oldParentIds}, \'%\')\r\n	&lt;/update&gt;\r\n	\r\n	&lt;!-- 删除子树 --&gt;\r\n	&lt;delete id=&quot;deleteSunTree&quot; parameterType=&quot;java.lang.String&quot;&gt;\r\n		delete from ${tableName}\r\n		where parent_ids like concat(${&quot;#&quot;}{oldParentIds}, \'%\')\r\n	&lt;/delete&gt;\r\n  \r\n&lt;/mapper&gt;', '72b2a96721894d018840c3be4da92fb9', '[entityName]Mapper.xml', '1');
